function renderCriticalError(title, details, err) {
	// was JQuery loaded? please
	if (window.$) {
		// NOTE: it's copy-paste from core.Application.onFailed
		var $root = $(".x-app-root");
		$root = $root.length ? $root : $("body");
		if (!$root.hasClass("x-app-root--failed")) {
			$root.empty().show().addClass("x-app-root--failed");
			var $container = $("<div class='x-app-init-container'></div>").appendTo($root);
			$("<h2 class='x-app-init-message'><div class='x-app-init-message-smile'>:(</div>" + title + "</h2><h3>" + details + "</h3>").appendTo($container);
			// if (that.config.isDebug) { // show the error somewhere }
		}
	} // else: write pure DOM manipulation logic by youself man
}
window.onerror = function (errMessage, source, lineno, colno, error) {
	var message;
	if (error && error.name === "SyntaxError") {
		message = navigator.language === "ru" ? "Обнаружен очень плохой javascript" : "Found very bad javascript";
	} else {
		message = navigator.language === "ru" ? "Что-то пошло не так..." : "Something bad happened...";
	}
	renderCriticalError(message, errMessage, error);
};
