IF EXISTS(SELECT * FROM sys.databases WHERE name='<%= dbName %>')
BEGIN
	ALTER DATABASE [<%= dbName %>]
	SET SINGLE_USER
	WITH ROLLBACK IMMEDIATE
	DROP DATABASE [<%= dbName %>]
END 
CREATE DATABASE <%= dbName %> on (name='<%= dbName %>', filename='<%= targetPath %>\App_Data\<%= dbName %>.mdf') COLLATE  Cyrillic_General_CI_AS