// Файл содержит описания заглушек для модулей, которые еще не переведены на TypeScript, но импортируются из других модулей.
// TODO: удалить этот файл, когда все модули будут переведены на TypeScript.

declare module "lib/ui/handlebars/handlebars-ext" {
	import HB = require("handlebars");
	const Handlebars: typeof HB;
	namespace Handlebars {
		//export type SafeString = hbs.SafeString;
		export interface SafeString extends hbs.SafeString {
		}

		export interface Template {
			(context: any, options?: { data?: any }): string;
		}

		export interface Helper {
			(context: any, options?: { data?: any, [key: string]: any }): any;
		}

		export interface HelperOptions {
			fn: Helper;
			inverse: Helper;
			hash: any;
			data?: any;
		}
	}
	export = Handlebars;
}

declare module "handlebars-ext" {
	import m = require("lib/ui/handlebars/handlebars-ext");
	export = m;
}
