export const ObjectListState = {
	initial: "initial" as "initial",
	reloading: "reloading" as "reloading",
	loadingMore: "loadingMore" as "loadingMore",
	loaded: "loaded" as "loaded",
	failed: "failed" as "failed",
	disposed: "disposed" as "disposed"
};

export type ObjectListState = (typeof ObjectListState)[keyof typeof ObjectListState];
