import $ = require("jquery");
import core = require("core");
import ReportPresenterBase = require("./ReportPresenterBase");
import ReportPart = require("./ReportPart");
import defaultTemplate = require("xhtmpl!modules/reporting/ui/templates/ReportPartPresenter.hbs");

import "lib/ui/ExpandablePanel";

import lang = core.lang;

class ReportPartPresenter extends ReportPresenterBase {
	static defaultOptions: ReportPartPresenter.Options = {
		template: defaultTemplate
	};

	/**
	 * @constructs ReportPartPresenter
	 * @extends ReportPresenterBase
	 */
	constructor(options: ReportPartPresenter.Options) {
		options = ReportPartPresenter.mixOptions(options, ReportPartPresenter.defaultOptions);
		super(options);
	}
}
namespace ReportPartPresenter {
	export interface Options extends ReportPresenterBase.Options {
	}
}

ReportPartPresenter.mixin({
	defaultOptions: ReportPartPresenter.defaultOptions
});

//core.reporting.ReportPartPresenter = ReportPartPresenter;
ReportPart.defaultOptions.Presenter = ReportPartPresenter;
export = ReportPartPresenter;


